def k_means_step(X, k, means):
    """
    A single update/step of the K-means algorithm
    Based on a input X and current mean estimate,
    predict clusters for each of the pixels and
    calculate new means.
    params:
    X = numpy.ndarray[numpy.ndarray[float]] - m x n | pixels x features (already flattened)
    k = int
    means = numpy.ndarray[numpy.ndarray[float]] - k x n

    returns:
    (new_means, clusters)
    new_means = numpy.ndarray[numpy.ndarray[float]] - k x n
    clusters = numpy.ndarray[int] - m sized vector
    """

    distances = []
    clusters = []
    new_means = []
#     calculate distances between means and all point and put distances into np array m
    for i in range(len(means)):
        d = np.linalg.norm(X-means[i], axis=1)
        distances.append(d)
    distances = np.array(distances)

#     find k index for each distance
    min_col_ids = np.argmin(distances, axis=0)
    distances = np.transpose(distances)

#     create k clusters
    for i in range(k):
        min_row_ids = np.argwhere(min_col_ids==np.ones(len(X))*i)
        c = np.take(X, min_row_ids, axis = 0)
        new_means.append(np.mean(c, axis = 0))
    new_means = np.array(new_means)
    new_means = np.squeeze(new_means, axis=1)

    return new_means, min_col_ids

