def k_means_segment(image_values, k=3, initial_means=None):
    """
    Separate the provided RGB values into
    k separate clusters using the k-means algorithm,
    then return an updated version of the image
    with the original values replaced with
    the corresponding cluster values.

    params:
    image_values = numpy.ndarray[numpy.ndarray[numpy.ndarray[float]]] - r x c x ch
    k = int
    initial_means = numpy.ndarray[numpy.ndarray[float]] or None

    returns:
    updated_image_values = numpy.ndarray[numpy.ndarray[numpy.ndarray[float]]] - r x c x ch
    """
    img_shape = image_values.shape
    if image_values.ndim == 3:
        image_values = np.reshape(image_values, (img_shape[0]*img_shape[1], 3))

#     get initial means
    if initial_means is None:
        initial_means = get_initial_means(image_values, k)

    n_iterations = 53
    shape = image_values.shape
    clusters = np.zeros([shape[1]])
    means_history = [initial_means]

#     run k means step to convergence
    for i in range(n_iterations):
        initial_means, clusters = k_means_step(image_values, k, initial_means)

#     segment image with mean values
    ones = np.ones((img_shape[0]*img_shape[1], 3))
    for i in range(k):
        mask = clusters == i
        mask = np.vstack((mask, mask, mask))
        mask = np.transpose(mask)
        prod = mask*ones*initial_means[i]
        if i == 0:
            img = prod
        else:
            img = img+prod

#     reshape image to original dimension
    img = np.reshape(img, (img_shape[0], img_shape[1], 3))

    return img
