def best_segment(X,k,iters, convergence_function=default_convergence):
    """Determine the best segmentation
    of the image by repeatedly
    training the model and
    calculating its likelihood.
    Return the segment with the
    highest likelihood.

    params:
    X = numpy.ndarray[numpy.ndarray[float]] - m x n
    k = int
    iters = int

    returns:
    (likelihood, segment)
    likelihood = float
    segment = numpy.ndarray[numpy.ndarray[float]]
    """
    initial_means, sigma, pi = initialize_parameters(X, k)
    init_likelihood = likelihood(X, pi, initial_means, sigma, k)
    initial_values = (initial_means, sigma, pi)
    for i in range(iters):
        new_MU, new_SIGMA, new_PI, r = train_model(X, k, convergence_function, initial_values)
        new_likelihood = likelihood(X, new_PI, new_MU, new_SIGMA, k)
        if (new_likelihood > init_likelihood):
            initial_values = (new_MU, new_SIGMA, new_PI)
            init_likelihood = new_likelihood
            new_r = r
    X_seg = segment(X, initial_values[0], k, new_r)

    return (init_likelihood, X_seg)
