def BIC_likelihood_model_test(image_matrix, comp_means):
    """Returns the number of components
    corresponding to the minimum BIC
    and maximum likelihood with respect
    to image_matrix and comp_means.

    params:
    image_matrix = numpy.ndarray[numpy.ndarray[float]] - m x n
    comp_means = list(numpy.ndarray[numpy.ndarray[float]]) - list(k x n) (means for each value of k)

    returns:
    (n_comp_min_bic, n_comp_max_likelihood)
    n_comp_min_bic = int
    n_comp_max_likelihood = int
    """
    l = []
    BICs = []
    for each in comp_means:
        PI = np.ones((1, len(each)))
        c = len(image_matrix)/np.sum(PI)
        PI = PI/np.sum(PI)
        sigma = compute_sigma(image_matrix, each)
        init_val = (each, sigma, PI)
        if PI.ndim == 2:
            PI = np.reshape(PI, (len(PI[0]),))
        MU, SIGMA, PI, r = train_model(image_matrix, len(each), convergence_function=default_convergence, initial_values=init_val)
        if PI.ndim == 2:
            PI = np.reshape(PI, (len(PI[0]),))
        l.append(likelihood(image_matrix, PI, MU, SIGMA, len(each)))
        if PI.ndim == 2:
            PI = np.reshape(PI, (len(PI[0]),))
        BICs.append(bayes_info_criterion(image_matrix, PI, MU, SIGMA, k))
    print(BICs)
    print(l)
    return (min(BICs), max(l))

